document.addEventListener('DOMContentLoaded',function(){const cursor=document.createElement('div');cursor.id='custom-cursor';document.body.appendChild(cursor);let mouseX=0,mouseY=0;document.addEventListener('mousemove',(e)=>{mouseX=e.clientX;mouseY=e.clientY;cursor.style.left=`${mouseX}px`;cursor.style.top=`${mouseY}px`;const target=e.target.closest('a, button, input, textarea, select, [role="button"], [onclick]');if(target){cursor.classList.add('hovering');}else{cursor.classList.remove('hovering');}
document.querySelectorAll('.magnetic').forEach(el=>{const rect=el.getBoundingClientRect();const elCenterX=rect.left+rect.width/2;const elCenterY=rect.top+rect.height/2;const distanceX=(mouseX-elCenterX)*0.2;const distanceY=(mouseY-elCenterY)*0.2;const isHovering=mouseX>rect.left&&mouseX<rect.right&&mouseY>rect.top&&mouseY<rect.bottom;if(isHovering){el.style.transform=`translate(${distanceX}px, ${distanceY}px)`;}else{el.style.transform=`translate(0, 0)`;}});});document.addEventListener('click',(e)=>{const ring=document.createElement('div');ring.className='cursor-ring';ring.style.left=e.clientX+'px';ring.style.top=e.clientY+'px';document.body.appendChild(ring);setTimeout(()=>{ring.remove();},800);});});