jQuery(document).ready(function($){const header=document.querySelector('.site-header');let lastScrollY=window.scrollY;function handleScroll(){const currentScroll=window.scrollY;if(currentScroll<=10){header.classList.remove('hidden','scrolled-up');header.classList.add('at-top');}else if(currentScroll>lastScrollY){header.classList.remove('scrolled-up','at-top');header.classList.add('hidden');}else{header.classList.remove('hidden','at-top');header.classList.add('scrolled-up');}
lastScrollY=currentScroll;}
window.addEventListener('scroll',handleScroll);const fadeTargets=document.querySelectorAll('.fade-in');fadeTargets.forEach((el)=>{gsap.fromTo(el,{opacity:0,y:30,},{opacity:1,y:0,duration:0.8,ease:'power2.out',scrollTrigger:{trigger:el,start:'top 90%',toggleActions:'play none none none',}});});$('#burger-toggle').on('click',function(){$('#site-navigation').toggleClass('active');});var swiper=new Swiper('.my-services-slider',{effect:'fade',speed:800,fadeEffect:{crossFade:true},slidesPerView:1,spaceBetween:0,loop:false,navigation:{nextEl:'.swiper-button-next',prevEl:'.swiper-button-prev',},pagination:{el:'.swiper-pagination',clickable:true,},});if(typeof gsap==='undefined'||typeof ScrollTrigger==='undefined'){console.warn('GSAP or ScrollTrigger not loaded');return;}
gsap.registerPlugin(ScrollTrigger);const cards=document.querySelectorAll('.project-card-wrapper');cards.forEach((card,index)=>{const directions=[{x:0,y:-150},{x:0,y:150},{x:-150,y:0},{x:150,y:0},];const dir=directions[index%directions.length];const rotateRand=gsap.utils.random(-10,10);gsap.fromTo(card,{opacity:0,x:dir.x,y:dir.y,rotate:rotateRand},{opacity:1,x:0,y:0,rotate:0,duration:1.2,ease:'power4.out',scrollTrigger:{trigger:card,start:'top 90%',toggleActions:'play none none none'}});});const snippets=document.querySelectorAll('.snippet-card');snippets.forEach((snippet,index)=>{const xOffset=gsap.utils.random(-100,100);const yOffset=gsap.utils.random(-150,150);const rotateX=gsap.utils.random(-10,10);const rotateY=gsap.utils.random(-10,10);gsap.fromTo(snippet,{opacity:0,x:xOffset,y:yOffset,rotateX,rotateY,scale:0.9,filter:'blur(6px)',transformPerspective:800,transformOrigin:'center'},{opacity:1,x:0,y:0,rotateX:0,rotateY:0,scale:1,filter:'blur(0px)',duration:1.6,ease:'power4.out',scrollTrigger:{trigger:snippet,start:'top 85%',toggleActions:'play none none none'},delay:index*0.1});});});